import React from "react";
import Row from "../Row";

function Pagination({param, data, rowInPage}) {
    let newRowInPage;
    const changePage = (event) => {
        event.preventDefault();
        let currentNumber = event.target.dataset["page"]
        newRowInPage = currentNumber*rowInPage
        let newPageUser = data.filter((user, index) => {
            if((index >= newRowInPage-rowInPage) && (index <= (currentNumber*rowInPage))) {
                return <Row data={user}/>
            }
        })
        setNewUsers(newPageUser)
    }

    const arrOfNumberPage = [];
    for(let i = 0; i<param; i++) {
        arrOfNumberPage.push(i+1)
    }

    return(
        <div className="pagination">
            {arrOfNumberPage.map(e => {
                return <a href="#" className="pagination__item" data-page={e} key={e} onClick={changePage}>{e}</a>
            })}
        </div>
    )
}

export default Pagination
